const { createBox, formatErrorMessage, formatSuccessMessage, formatWarningMessage, formatInfoMessage } = require('../../../lib/utils/display');

describe('Display Utils', () => {
  describe('createBox', () => {
    it('should create a box with content', () => {
      const result = createBox('Test content');
      expect(result).toContain('Test content');
    });

    it('should apply custom options', () => {
      const result = createBox('Test content', {
        borderColor: 'blue',
        padding: 2
      });
      expect(result).toContain('Test content');
    });
  });

  describe('formatters', () => {
    it('should format error message', () => {
      const result = formatErrorMessage('Test error');
      expect(result).toBe('Error: Test error');
    });

    it('should format success message', () => {
      const result = formatSuccessMessage('Test success');
      expect(result).toBe('Test success');
    });

    it('should format warning message', () => {
      const result = formatWarningMessage('Test warning');
      expect(result).toBe('Warning: Test warning');
    });

    it('should format info message', () => {
      const result = formatInfoMessage('Test info');
      expect(result).toBe('Test info');
    });
  });
});